sysrule_assignment.script:
/**
* Example:
* The following script requires personalizing the instance to add the Malware category and the Security assignment group.
* if (current.category == "Hardware")
*    current.assignment_group.setDisplayValue("Hardware"); 
* else if (current.category == "Software")
*    current.assignment_group.setDisplayValue("Software");
* else if (current.category == "Malware")
*    current.assignment_group.setDisplayValue("Security");	
*
* Another Example:
* Release Planning Example, which assigns the last person assigned to a release to the current release.
* current.release.product.service.assigned_to;
*/


var grMembers = new GlideRecord("sys_user_grmember");
grMembers.addQuery("group", "7030dbac8354e210331e6ed0deaad326"); 
grMembers.query();

var array = [];

// Add them all to an array

while (grMembers.next()) {

var user = grMembers.user.toString();
array.push(user);
}

var arrayLen = array.length;

// Randomly select the user from the elements in the array
var randomUser = array[Math.floor(Math.random() * arrayLen)];

var userName = new GlideRecord("sys_user");
userName.get(randomUser);

// This is optional, but could be helpful
current.work_notes = " Auto assigning " + current.number + " to random user: " + userName.first_name + " " + userName.last_name;

// Set the randomly found user in the group
current.assigned_to = randomUser;
